webpackJsonp([12],{859:function(t,n){t.exports='/*{\n\t"CREDIT": "by v002",\n\t"ISFVSN": "2",\n\t"CATEGORIES": [\n\t\t"Halftone Effect", "v002"\n\t],\n\t"INPUTS": [\n\t\t{\n\t\t\t"NAME": "inputImage",\n\t\t\t"TYPE": "image"\n\t\t},\n\t\t{\n\t\t\t"NAME": "invert",\n\t\t\t"TYPE": "float",\n\t\t\t"MIN": 0.0,\n\t\t\t"MAX": 1.0,\n\t\t\t"DEFAULT": 0.0\n\t\t},\n\t\t{\n\t\t\t"NAME": "separation",\n\t\t\t"TYPE": "float",\n\t\t\t"MIN": 0.0,\n\t\t\t"MAX": 0.5,\n\t\t\t"DEFAULT": 0.25\n\t\t},\n\t\t{\n\t\t\t"NAME": "greyscale",\n\t\t\t"TYPE": "float",\n\t\t\t"MIN": 0.0,\n\t\t\t"MAX": 1.0,\n\t\t\t"DEFAULT": 0.75\n\t\t},\n\t\t{\n\t\t\t"NAME": "thickness",\n\t\t\t"TYPE": "float",\n\t\t\t"MIN": 0.5,\n\t\t\t"MAX": 1.0,\n\t\t\t"DEFAULT": 0.75\n\t\t},\n\t\t{\n\t\t\t"NAME": "front",\n\t\t\t"TYPE": "color",\n\t\t\t"DEFAULT": [\n\t\t\t\t0.5,\n\t\t\t\t0.0,\n\t\t\t\t0.25,\n\t\t\t\t1.0\n\t\t\t]\n\t\t},\n\t\t{\n\t\t\t"NAME": "back",\n\t\t\t"TYPE": "color",\n\t\t\t"DEFAULT": [\n\t\t\t\t0.2,\n\t\t\t\t0.75,\n\t\t\t\t0.5,\n\t\t\t\t1.0\n\t\t\t]\n\t\t}\n\t]\n}*/\n\n\n//Original Source\n//http://learningwebgl.com/blog/?p=2858\n\n//\tBased on https://github.com/v002/v002-Half-Tones/\n\n\n\n\nconst vec4 lumacoeff = vec4(0.2126, 0.7152, 0.0722, 0.0);\n\n\nvoid main()\n{\n    vec4 color = IMG_THIS_PIXEL(inputImage);\n    float lum = dot(color, lumacoeff);\n    vec2 coord = gl_FragCoord.xy;\n    \n    vec4 colora = mix(front, back, invert);\n    vec4 colorb = mix(back, front, invert);\n    \n    colora.a *= color.a;\n    colorb.a *= color.a;\n    \n    vec4 cout = mix(color,colora, greyscale);\n\n    gl_FragColor = colorb;\n    \n    if (lum > 1.00)\n    {\n        if (mod(coord.x + coord.y, thickness) >= separation)\n            gl_FragColor = cout;\n    }\n    \n    if (lum > 0.75)\n    {\n        if (mod(coord.x - coord.y, thickness) >= separation )\n            gl_FragColor = cout;\n    }\n    \n    if (lum > 0.50)\n    {\n        if (mod(coord.x + coord.y - (thickness * 0.5), thickness) >= separation)\n            gl_FragColor = cout;\n    }\n    \n    if (lum > 0.3)\n    {\n        if (mod(coord.x - coord.y - (thickness * 0.5), thickness) >= separation)\n            gl_FragColor = cout;\n    }\n}'}});